class Translation_System


  def activate_translation
    Window_Journal.class_eval do
      def draw_item(index)
        item = @data[index]
        rect = Rect.new(10, @n, 460, 32)
        self.contents.fill_rect(rect, Color.new(0,0,0,0))    
        self.contents.draw_text(2, @n, 425, 32, item, 0)
        @n += 32
      end
    end
  end


  def deactivate_translation
    Window_Journal.class_eval do
      def draw_item(index)
        item = @data[index]
        rect = Rect.new(10, @n, 460, 32)
        self.contents.fill_rect(rect, Color.new(0,0,0,0))    
        self.contents.draw_text(10, @n, 460, 32, "●", 0)
        self.contents.draw_text(25, @n, 460, 32, item, 0)
        @n += 32
      end
    end
  end


end


